import React,{useState} from 'react';
import Button from '../row_btn/Button';
import axios from 'axios';
import Loading from '../loading/Loading'
export default function ProductItem({product,isAdmin,setCallback,callback,token}) {
  const [loading,setLoading] = useState(false)

  const deleteProduct = async () =>{
    try {
      setLoading(true)
      const deleteImage = axios.post("/api/destroy",{public_id:product.images.public_id},{
        headers:{Authorization:token}
      })
      const deleteProductData = axios.delete(`/api/product/${product._id}`,{
        headers:{Authorization:token}
      })
      await deleteImage
      await deleteProductData
      setLoading(false)
      setCallback(!callback)
      
    } catch (err) {
      alert(err.response.data.msg)
    }
  }
  return (
    <div className='product_card'>
      { isAdmin && <input type="checkbox" checked={product.checked}/>}
        <img src={product.images.url} alt='' />
       <div className='product_box'>
            <h2 title={product.title}>{product.title}</h2>
            <span>${product.price}</span>
            <p>${product.description}</p>
       </div>
       <Button product={product} deleteProduct={deleteProduct}/>
    </div>
  );
}
