import axios from 'axios';
import React, { useContext, useState } from 'react'
import { GlobleState } from '../../../GlobleState';
export default function Status() {
    const state = useContext(GlobleState)
    const [token] = state.token
    const [status,setstatus] = useState('Processing')
    console.log(status)

    const myFunction = (e) => {
        setstatus(e.target.value)
    }
    const handleStatus = async (e) => {
        e.preventDefault();
        try{
         const res = await axios.patch("/api/payment/6243dd2803f66b380b73b6dc",{status:status},{
             headers:{Authorization:token}
         })
         alert(res.data.msg)
        }catch(err){
            alert(err.response.data.msg)
        }
    }

  return (
    <div>
    <form onSubmit={handleStatus}>
        <label>Choose a car:</label>
        <select name="cars" id="cars" onChange={myFunction}>
            <option value="volvo">Processing</option>
            <option value="saab">Shiping</option>
            <option value="opel">Delivered</option>
           
        </select>
        <br/>
        <input type="submit" value="Submit"/>
    </form>
    <iframe width="560" height="315" src="https://www.youtube.com/embed/uXl77UFkrkQ" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
    <iframe src="https://www.facebook.com/plugins/video.php?height=476&href=https%3A%2F%2Fwww.facebook.com%2F101883949157240%2Fvideos%2F4539242842857548%2F&show_text=false&width=476&t=0" width="476" height="476" style="border:none;overflow:hidden" scrolling="no" frameborder="0" allowfullscreen="true" allow="autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share" allowFullScreen="true"></iframe>
    </div>
  )
}
