import React,{useContext, useEffect, useState} from 'react';
import {GlobleState} from '../../../GlobleState'
import ProductItem from '../utiles/productItem/ProductItem'
import Loading from '../utiles/loading/Loading';


export default function Products() {
  const state = useContext(GlobleState)
  const [products] = state.productsAPI.products 
  const [isAdmin] = state.userApi.isAdmin
  const [callback,setCallback] = state.productsAPI.callback
  const [token] = state.token
  

 useEffect(()=>{
   document.title = 'Shop'
 })
  return <div className='products'>
     {
       products.map((product)=>{
          
         return(
           <ProductItem key={product._id} product={product} isAdmin={isAdmin} callback={callback} setCallback={setCallback} token={token} />
          
         )     
       })
       
     }
     
      {
      products.length === 0 && <Loading/>
      }
      
    
  </div>;
}
