import axios from 'axios';
import React, { useContext, useState } from 'react'
import { GlobleState } from '../../../GlobleState';
export default function Status() {
    const state = useContext(GlobleState)
    const [token] = state.token
    const [status,setstatus] = useState('')
    console.log(status)

    const myFunction = (e) => {
        setstatus(e.target.value)
    }
    const handleStatus = async (e) => {
        e.preventDefault();
        try{
         const res = await axios.patch("/api/payment/6243dd2803f66b380b73b6dc",{status:status},{
             headers:{Authorization:token}
         })
         alert(res.data.msg)
        }catch(err){
            alert(err.response.data.msg)
        }
    }

  return (
    <div>
    <form onSubmit={handleStatus}>
        <label>Choose a car:</label>
        <select name="cars" id="cars" onChange={myFunction}>
            <option value="volvo">Processing</option>
            <option value="saab">Shiping</option>
            <option value="opel">Delivered</option>
           
        </select>
        <br/>
        <input type="submit" value="Submit"/>
    </form>
    </div>
  )
}
